// === Pobranie wejÅ›cia ===
const items = $input.all();
if (!items || items.length === 0) {
  throw new Error('No input items available in this node');
}

const input = items[0].json || {};

// === WyciÄ…gniÄ™cie liczby z rÃ³Å¼nych moÅ¼liwych formatÃ³w ===
let injectionScore = 0.5; // domyÅ›lna wartoÅ›Ä‡

// PrÃ³buj wyciÄ…gnÄ…Ä‡ wartoÅ›Ä‡ z rÃ³Å¼nych moÅ¼liwych lokalizacji
if (typeof input === 'number') {
  injectionScore = input;
} else if (input.output !== undefined) {
  // input.output moÅ¼e byÄ‡ stringiem lub liczbÄ…
  injectionScore = typeof input.output === 'string' ? parseFloat(input.output) : input.output;
} else if (input.choices && input.choices[0]) {
  const choice = input.choices[0];
  const value = choice.text || choice.message?.content || choice;
  injectionScore = typeof value === 'string' ? parseFloat(value) : value;
} else if (input.result !== undefined) {
  injectionScore = typeof input.result === 'string' ? parseFloat(input.result) : input.result;
}

// JeÅ›li wciÄ…Å¼ mamy string, parsuj go
if (typeof injectionScore === 'string') {
  injectionScore = parseFloat(injectionScore);
}

// Walidacja i ograniczenie do zakresu 0-1
injectionScore = isNaN(injectionScore) ? 0.5 : injectionScore;
injectionScore = Math.max(0, Math.min(1, injectionScore));

// === Progi konfiguracyjne ===
const config = {
  blockThreshold: 0.5,
  warnThreshold: 0.3,
  logThreshold: 0.1
};

// === OkreÅ›lenie poziomu ryzyka na podstawie rozkÅ‚adu wartoÅ›ci ===
// Progi klasyfikacji:
// 0.8 - 1.0 = CRITICAL (bardzo wysokie zagroÅ¼enie)
// 0.6 - 0.8 = HIGH (wysokie zagroÅ¼enie)
// 0.4 - 0.6 = MEDIUM (Å›rednie zagroÅ¼enie)
// 0.2 - 0.4 = LOW (niskie zagroÅ¼enie)
// 0.0 - 0.2 = MINIMAL (minimalne zagroÅ¼enie)
const riskLevel = injectionScore >= 0.8 ? 'CRITICAL' :
                  injectionScore >= 0.6 ? 'HIGH' :
                  injectionScore >= 0.4 ? 'MEDIUM' :
                  injectionScore >= 0.2 ? 'LOW' : 
                  'MINIMAL';

// === OkreÅ›lenie akcji i routingu ===
let action, route, message;

if (injectionScore >= config.blockThreshold) {
  action = 'BLOCK';
  route = 'danger';
  message = 'ðŸš« Wykryto potencjalnie niebezpieczne elementy w zapytaniu.';
} else if (injectionScore >= config.warnThreshold) {
  action = 'WARN_AND_ALLOW';
  route = 'warning';
  message = 'âš ï¸ Przetwarzam zapytanie z dodatkowymi Å›rodkami ostroÅ¼noÅ›ci.';
} else if (injectionScore >= config.logThreshold) {
  action = 'ALLOW_WITH_LOG';
  route = 'safe';
  message = null;
} else {
  action = 'ALLOW';
  route = 'safe';
  message = null;
}

// === Przygotowanie wyniku ===
const result = {
  // GÅ‚Ã³wne dane
  injectionScore: injectionScore,
  riskLevel: riskLevel,
  route: route,
  action: action,
  isSafe: route === 'safe',

  // WiadomoÅ›Ä‡ dla uÅ¼ytkownika (jeÅ›li potrzebna)
  userMessage: message,

  // Metadane
  metadata: {
    thresholds: config,
    timestamp: new Date().toISOString(),
    rawInput: input
  }
};

// ZwrÃ³Ä‡ wynik jako item z polem json
return [{ json: result }];

// Enhanced Configuration Node v4.1.0 - For Sequential Sanitizer
const items = $input.all();

// Define comprehensive sanitizer configuration (safe defaults + retry/backoff/timeouts)
const sanitizerConfig = {
  // Core security features
  ENABLE_UNICODE_NORMALIZATION: true,
  ENABLE_HOMOGLYPH_DETECTION: true,
  ENABLE_TOKEN_FRAGMENTATION_DETECTION: true,

  // Advanced features (disabled by default)
  ENABLE_EMBEDDING_HOOKS: false,
  ENABLE_LLM_GATEKEEPER_HOOKS: false,

  // Threat detection thresholds
  CRITICAL_THRESHOLD: 70,
  HIGH_THRESHOLD: 40,
  MEDIUM_THRESHOLD: 20,

  // Processing limits
  MAX_INPUT_LENGTH: 10000,
  MAX_PROCESSING_TIME: 5000,
  MAX_DECODE_ITERATIONS: 10,

  // Retry/backoff defaults for any external IO used by workflow
  RETRY_ATTEMPTS: 3,
  RETRY_BACKOFF_MS: 500,
  RETRY_BACKOFF_FACTOR: 2,
  HANDLE_429_RETRY: true,
  DEFAULT_TIMEOUT_MS: 10000,

  // Output configuration
  STRICT_OUTPUT_MODE: true,
  SANITIZE_LEVEL: 'maximum',

  // Audit and logging
  ENABLE_AUDIT_LOGGING: true,
  INCLUDE_RAW_MATCHES: true,
  LOG_AUDIT: true
};

// Process each item and add configuration
for (const item of items) {
  if (!item.json) {
    item.json = {};
  }

  item.json.sanitizer_config = sanitizerConfig;
  item.json.config_version = '4.1.0';
  item.json.config_timestamp = new Date().toISOString();
  item.json.features_enabled = {
    unicode_normalization: sanitizerConfig.ENABLE_UNICODE_NORMALIZATION,
    homoglyph_detection: sanitizerConfig.ENABLE_HOMOGLYPH_DETECTION,
    token_fragmentation: sanitizerConfig.ENABLE_TOKEN_FRAGMENTATION_DETECTION,
    sequential_processing: true
  };
}

return items;

// INPUT VALIDATION (robust)
const items = $input.all();
const out = [];

for (const inputItem of items) {
  try {
    const json = inputItem.json || {};
    const inputText = (json.message || json.input || json.text || '').toString();
    const start = Date.now();

    const result = {
      originalInput: inputText,
      length: inputText.length,
      isEmpty: !inputText.trim(),
      isSafe: false,
      skipAdvanced: false,
      hasQuickThreats: false,
      suspiciousUnicode: false,
      processingTime: 0
    };

    if (result.isEmpty) {
      result.isSafe = true;
      result.skipAdvanced = true;
    }

    if (result.length > (json.sanitizer_config?.MAX_INPUT_LENGTH || 10000)) {
      result.originalInput = inputText.slice(0, json.sanitizer_config?.MAX_INPUT_LENGTH || 10000);
      result.length = result.originalInput.length;
    }

    const safePatterns = [/^(what|how|when|where|who|why)\\b/i, /^(can you|could you|please)\\s+(help|write|create)/i, /^(thank you|thanks)\\b/i];
    result.matchesSafePattern = safePatterns.some(p => p.test(inputText));

    const quickThreats = ['ignore', 'system:', 'assistant:', 'jailbreak', 'bypass', 'reveal', 'show me'];
    result.hasQuickThreats = quickThreats.some(t => inputText.toLowerCase().includes(t));

    const suspiciousUnicode = /[\\u0400-\\u04FF\\u0370-\\u03FF\\uFF00-\\uFFEF\\u200B-\\u200D]/.test(inputText);
    result.suspiciousUnicode = suspiciousUnicode;

    // Skip advanced only if safe AND no quickThreats AND no suspiciousUnicode
    result.skipAdvanced = result.matchesSafePattern && !result.hasQuickThreats && !result.suspiciousUnicode;

    result.processingTime = Date.now() - start;
    out.push({ json: { ...(json || {}), validation: result } });
  } catch (err) {
    out.push({ json: { __error: err && err.message ? err.message : String(err) } });
  }
}

return out;

// UNICODE NORMALIZATION + UNESCAPE \uXXXX + HOMOGLYPH DEOBFUSCATION
const items = $input.all();

// small homoglyph map (extendable)
const HMAP = {
  '\u0438':'i','\u0456':'i','\u0441':'s','\u03B1':'a','\u03C1':'p',
  '\uff41':'a','\uff49':'i','\uff53':'s'
};

// unescape \uXXXX sequences -> real unicode character
function unescapeUnicodeEscapes(s){
  if(!s || typeof s !== 'string') return s;
  return s.replace(/\\u([0-9a-fA-F]{4})/g, (_, hex) => String.fromCharCode(parseInt(hex, 16)));
}

function normalizeText(t){
  if(!t) return { normalized:'', changes:[], homoglyphsDetected:false };
  // First: unescape literal \uXXXX sequences that may be present in tests
  let n = unescapeUnicodeEscapes(String(t));
  // NFKC normalization if available
  if (typeof n.normalize === 'function') n = n.normalize('NFKC');
  // remove zero-width and BOM
  n = n.replace(/[\\u200B-\\u200D\\uFEFF]/g,'');
  const changes = [];
  let rebuilt = '';
  for (const ch of n) {
    if (HMAP[ch]) { rebuilt += HMAP[ch]; changes.push({from:ch,to:HMAP[ch]}); }
    else rebuilt += ch;
  }
  rebuilt = rebuilt.replace(/\\s+/g,' ').trim();
  return { normalized: rebuilt, changes, homoglyphsDetected: changes.length>0 };
}

const out = [];
for(const item of items){
  try {
    const json = item.json || {};
    const inputText = json.validation?.originalInput || '';
    const start = Date.now();
    const normalization = normalizeText(inputText);
    item.json = item.json || {};
    item.json.normalization = { ...normalization, processingTime: Date.now()-start };
    out.push(item);
  } catch (err) {
    out.push({ json: { __error: err && err.message ? err.message : String(err) } });
  }
}
return out;

// FAST PATTERN DETECTION (lightweight regex checks) - extended social patterns
const items = $input.all();
const fastPatterns = [
  /ignore previous instructions/i,
  /system:/i,
  /assistant:/i,
  /(unrestricted|bypass all)/i,
  /reveal\\s+(?:system|prompt|secrets)/i,
  // new: reveal ... system/prompt anywhere later in sentence
  /reveal\\s+.*\\b(system|prompt|secrets|configuration|instructions)\\b/i,
  // social engineering patterns
  /\\b(tell me|please tell me|show me|can you show me|can you tell me)\\b.*\\b(system|prompt|instructions|configuration|secret|secrets)\\b/i,
  /\\b(how are you instructed|how you are instructed|how you were instructed)\\b/i
];
const out = [];

for (const item of items) {
  try {
    const json = item.json || {};
    const txt = (json.normalization?.normalized || json.validation?.originalInput || '').toString();
    const start = Date.now();
    const matches = fastPatterns.filter(p => p.test(txt)).map(p => p.toString());
    item.json = item.json || {};
    item.json.fastDetection = { score: Math.min(100, matches.length * 30), matches, processingTime: Date.now() - start };
    out.push(item);
  } catch (err) {
    out.push({ json: { __error: err && err.message ? err.message : String(err) } });
  }
}
return out;

// Advanced Pattern Detection - full jsCode (debuggable + standard output fields)
