const items = $input.all();

// helper: escape regex metacharacters for safe building
function esc(s) {
  return s.replace(/[-\/\\^$*+?.()|[\]{}]/g, '\\$&');
}

// build zero-width regex via fromCharCode (avoid \u literals)
function buildZeroWidth() {
  const chars = [String.fromCharCode(0x200B), String.fromCharCode(0x200C), String.fromCharCode(0x200D), String.fromCharCode(0xFEFF)];
  const joined = chars.map(c => c.replace(/[-\\\]\^]/g, '\\$&')).join('');
  return new RegExp('[' + joined + ']', 'u');
}
const zeroWidth = buildZeroWidth();

// textual \uXXXX (backslash-u in text)
const literalUnicodeEscape = new RegExp('\\\\u([0-9A-Fa-f]{4})', 'i');

// fragmentation builder (tolerant)
function frag(word) {
  const letters = word.split('').map(ch => esc(ch));
  const joiner = '(?:[\\s\\W_]*)';
  return new RegExp(letters.join(joiner), 'iu');
}

// aggressive fragmentation allowing any non-alnum between letters
function aggressiveFrag(word) {
  const parts = word.split('').map(ch => esc(ch));
  const middle = '[^A-Za-z0-9]*';
  const core = parts.join(middle);
  const pattern = '(?:^|[^A-Za-z0-9])' + core + '(?:[^A-Za-z0-9]|$)';
  return new RegExp(pattern, 'i');
}

// comment pattern built from string (safer)
const commentPatternStr = '(\\/\\*[\\s\\S]*?\\*\\/)|(\\/\\/.*$)|(^|\\n|\\r)#.*$';
const commentRegex = new RegExp(commentPatternStr, 'm');

// detection table (safe RegExp objects)
const detectors = [
  { r: frag('ignore'), weight: 40, id: 'fragmentation_ignore' },
  { r: aggressiveFrag('ignore'), weight: 50, id: 'aggr_fragment_ignore' },
  { r: frag('actasystem'), weight: 40, id: 'fragment_actasystem' },
  { r: aggressiveFrag('actasystem'), weight: 40, id: 'aggr_fragment_actasystem' },
  { r: new RegExp('i\\s*g\\s*n\\s*o\\s*r\\s*e', 'i'), weight: 40, id: 'space_ignore' },
  { r: new RegExp('pre\\s*-?\\s?vious', 'i'), weight: 25, id: 'previous_frag' },
  { r: commentRegex, weight: 40, id: 'comment_injection' },
  { r: literalUnicodeEscape, weight: 40, id: 'literal_unicode_escape' },
  { r: zeroWidth, weight: 40, id: 'zero_width' },
  { r: new RegExp('#{3,}'), weight: 40, id: 'delimiter_hash' },
  { r: new RegExp('-{3,}'), weight: 40, id: 'delimiter_dash' },
  { r: new RegExp('\\*{3,}'), weight: 40, id: 'delimiter_star' },
  { r: new RegExp('<\\|system\\||<\\|user\\||<\\|assistant\\|', 'i'), weight: 40, id: 'chat_template' },
  { r: new RegExp('\\breveal\\b.*\\b(system|prompt|secrets|configuration|instructions)\\b', 'i'), weight: 40, id: 'reveal_system' },
  { r: new RegExp('\\b(dan\\s*mode|developer\\s*mode|unrestricted|bypass\\s+all)\\b', 'i'), weight: 30, id: 'jailbreak' }
];

// Function: gather candidate text fields into one string (debuggable)
function gatherTextCandidates(json) {
  const candidates = [];
  if (json.normalization && typeof json.normalization.normalized === 'string') candidates.push(json.normalization.normalized);
  if (json.validation && typeof json.validation.originalInput === 'string') candidates.push(json.validation.originalInput);
  if (json.chatInput && typeof json.chatInput === 'string') candidates.push(json.chatInput);
  if (json.inputText && typeof json.inputText === 'string') candidates.push(json.inputText);
  if (json.clearinput && typeof json.clearinput === 'string') candidates.push(json.clearinput);
  if (json.clearoutput && typeof json.clearoutput === 'string') candidates.push(json.clearoutput);
  if (json.raw && typeof json.raw === 'string') candidates.push(json.raw);
  if (json.text && typeof json.text === 'string') candidates.push(json.text);
  try {
    for (const k of Object.keys(json)) {
      const v = json[k];
      if (typeof v === 'string' && v.length && candidates.indexOf(v) === -1) candidates.push(v);
    }
  } catch(e) {}
  return candidates.join(' \\n| ');
}

// MAIN
const out = [];

for (const item of items) {
  try {
    item.json = item.json || {};
    const json = item.json;

    // collect and expose for debugging
    const candidateText = gatherTextCandidates(json);
    item.json.debugTextCandidates = candidateText;
    item.json.debugFields = {};
    item.json.debugFields.candidateLength = candidateText.length;
    const firstSlice = candidateText.slice(0, 120);
    item.json.debugFields.firstChunk = firstSlice;
    item.json.debugFields.firstChunkCharCodes = Array.from(firstSlice).map(ch => ch.charCodeAt(0)).slice(0, 60);

    const txt = String(candidateText || '');

    const advanced = { skipped: false, matches: [], score: 0, processingTime: 0, matchedId: null };

    const skip = !!(json.validation && json.validation.skipAdvanced);
    const force = !!(json.validation && json.validation.suspiciousUnicode) || !!(json.fastDetection && json.fastDetection.score > 0) || !!json.validation?.hasQuickThreats;

    const start = Date.now();
    if (skip && !force) {
      advanced.skipped = true;
    } else {
      for (const d of detectors) {
        try {
          if (d.r && d.r.test && d.r.test(txt)) {
            advanced.matches.push(d.id);
            advanced.score += (d.weight || 25);
            if (!advanced.matchedId) advanced.matchedId = d.id;
          }
        } catch (re) {
          advanced.matches.push('__regex_error:' + (re && re.message ? re.message : String(re)));
        }
      }
    }
    advanced.processingTime = Date.now() - start;
    advanced.score = Math.min(100, advanced.score);
    advanced.detected = advanced.matches.length > 0;

    // Standardized output fields for downstream nodes
    item.json.advancedDetection = advanced;
    item.json.threatDetected = !!advanced.detected;
    item.json.threatScore = advanced.score || 0;
    item.json.threatMatches = advanced.matches || [];
    item.json.threatMatchedId = advanced.matchedId || null;

    out.push(item);
  } catch (err) {
    out.push({ json: { __error: err && err.message ? err.message : String(err) } });
  }
}

return out;

// Threat Scoring & Classification - full jsCode
// Input: items from Advanced Detection/Content Sanitization
// Output: items with standardized item.json.result = { detected, score, severity, labels, reason }

const items = $input.all();

function safeNumber(v, def=0){
  if (v === undefined || v === null) return def;
  const n = Number(v);
  return Number.isFinite(n) ? n : def;
}

for (const item of items){
  try {
    item.json = item.json || {};
    const cfg = item.json.sanitizer_config || item.json.config || {};
    const CRITICAL_THRESHOLD = safeNumber(cfg.CRITICAL_THRESHOLD, 70);
    const HIGH_THRESHOLD = safeNumber(cfg.HIGH_THRESHOLD, 40);
    const MEDIUM_THRESHOLD = safeNumber(cfg.MEDIUM_THRESHOLD, 20);

    // Prefer explicit boolean if present
    let detected = (typeof item.json.threatDetected === 'boolean') ? item.json.threatDetected : undefined;
    let score = safeNumber(item.json.threatScore, undefined);
    // fallback to advancedDetection.score
    if ((score === undefined || score === 0) && item.json.advancedDetection && typeof item.json.advancedDetection.score === 'number'){
      score = safeNumber(item.json.advancedDetection.score, 0);
    }
    // fallback: if score missing but matches array exists â€” compute heuristic
    if ((score === undefined || score === 0) && Array.isArray(item.json.threatMatches) && item.json.threatMatches.length){
      score = Math.min(100, item.json.threatMatches.length * 30);
    }
    // final fallback: 0
    score = safeNumber(score, 0);

    // Determine detected if not explicitly provided
    if (detected === undefined){
      detected = score >= HIGH_THRESHOLD; // treat >=HIGH as detected by default
    }

    // severity mapping
    let severity = 'none';
    if (score >= CRITICAL_THRESHOLD) severity = 'critical';
    else if (score >= HIGH_THRESHOLD) severity = 'high';
    else if (score >= MEDIUM_THRESHOLD) severity = 'medium';
    else if (score > 0) severity = 'low';

    // labels (copy matches with safe normalization)
    const labels = Array.isArray(item.json.threatMatches) ? item.json.threatMatches.slice(0) : (item.json.advancedDetection && Array.isArray(item.json.advancedDetection.matches) ? item.json.advancedDetection.matches.slice(0) : []);

    // flaggedByScoreThreshold: true if score >= CRITICAL_THRESHOLD (configurable)
    const flaggedByScoreThreshold = score >= CRITICAL_THRESHOLD;
    // flaggedByScoring: an additional heuristic if severity is at least medium
    const flaggedByScoring = (severity === 'critical' || severity === 'high' || severity === 'medium');

    // Provide canonical result object for aggregator
    item.json.result = {
      detected: !!detected,
      score: score,
      severity: severity,
      labels: labels,
      flaggedByScoreThreshold: !!flaggedByScoreThreshold,
      flaggedByScoring: !!flaggedByScoring,
      reason: item.json.threatMatchedId || (labels[0] || null)
    };

    // keep backward compat fields
    item.json.threatDetected = !!item.json.result.detected;
    item.json.threatScore = item.json.result.score;

  } catch (err) {
    item.json = item.json || {};
    item.json.result = item.json.result || {};
    item.json.result.error = err && err.message ? err.message : String(err);
  }
}

return items;

// Content Sanitization (extended) - full jsCode
