const items = $input.all();

function escapeForRegex(s){ return s.replace(/[-\/\\^$*+?.()|[\]{}]/g, '\\$&'); }

// build zero-width regex via fromCharCode (avoid \u literals)
function buildZeroWidthRegex(){
  const chars=[String.fromCharCode(0x200B),String.fromCharCode(0x200C),String.fromCharCode(0x200D),String.fromCharCode(0xFEFF)];
  const joined = chars.map(c => c.replace(/[-\\\]\^]/g,'\\$&')).join('');
  return new RegExp('['+joined+']','u');
}
const zeroWidthClass = buildZeroWidthRegex();

// literal \uXXXX in text
const literalUnicodeEscape = new RegExp('\\\\u([0-9A-Fa-f]{4})','gi');

function fragmentationRegexForWord(word){
  const letters = word.split('').map(ch => escapeForRegex(ch));
  const joiner = '(?:[\\\\s\\\\W_]*)';
  const pattern = '\\b' + letters.join(joiner) + '\\b';
  return new RegExp(pattern,'iu');
}

const fragMap = [
  { id:'ignore_instructions', words:['ignore previous instructions','ignore'], placeholder:'[filtered:ignore_instructions]' },
  { id:'act_as_system', words:['act as system','actasystem','act as system'], placeholder:'[filtered:act_as_system]' }
];

const commentPatternStr = '(\\/\\*[\\s\\S]*?\\*\\/)|(\\/\\/.*$)|(^|\\n|\\r)#.*$';
const commentRegex = new RegExp(commentPatternStr,'gim');

const patternToPlaceholder = [
  { id:'system_prompt', regex:new RegExp('(^|\\s)system:','gi'), placeholder:'[filtered:system_prompt]' },
  { id:'reveal_terms', regex:new RegExp('\\breveal\\s+(?:system|prompt|secrets|configuration|instructions)\\b','gi'), placeholder:'[filtered:reveal_terms]' },
  { id:'jailbreak', regex:new RegExp('\\b(?:dan\\s?mode|developer\\s?mode|unrestricted|bypass\\s+all)\\b','gi'), placeholder:'[filtered:jailbreak_terms]' }
];

for (const item of items){
  try{
    item.json = item.json || {};
    // --- primary source selection: prefer normalized, then validation.originalInput, then chatInput/inputText/clearinput
    const srcCandidates = [
      (item.json.normalization && item.json.normalization.normalized) || '',
      (item.json.validation && item.json.validation.originalInput) || '',
      item.json.chatInput || '',
      item.json.inputText || '',
      item.json.clearinput || '',
      item.json.clearoutput || ''
    ];
    const primary = srcCandidates.find(s=>s && String(s).trim().length>0) || '';
    let outText = String(primary || '');

    // If primary empty but chatInput/inputText present, copy into validation.originalInput
    if(!outText && item.json.chatInput){ outText = String(item.json.chatInput); item.json.validation = item.json.validation||{}; item.json.validation.originalInput = outText; }
    if(!outText && item.json.inputText){ outText = String(item.json.inputText); item.json.validation = item.json.validation||{}; item.json.validation.originalInput = outText; }

    const replacements = [];

    // 1) Remove zero-width characters early
    const beforeZW = outText;
    outText = outText.replace(zeroWidthClass, '');
    if (beforeZW !== outText) replacements.push({ originalPattern: 'zero_width_chars', replacement: '[filtered:zero_width_removed]' });

    // 2) Handle textual \uXXXX sequences: attempt to unescape to real char
    const literalMatches = [];
    outText = outText.replace(literalUnicodeEscape, (m, g1) => {
      literalMatches.push(g1);
      try {
        return String.fromCharCode(parseInt(g1, 16));
      } catch (e) {
        return m;
      }
    });
    if (literalMatches.length) {
      replacements.push({ originalPattern: 'literal_unicode_escape', replacement: '[filtered:literal_unicode_unescaped]' });
    }

    // 3) De-fragmentation for known sensitive phrases
    for (const fm of fragMap) {
      for (const w of fm.words) {
        const fragRe = fragmentationRegexForWord(w);
        const before = outText;
        outText = outText.replace(fragRe, fm.placeholder);
        if (before !== outText) replacements.push({ originalPattern: fm.id, replacement: fm.placeholder });
      }
    }

    // 4) Generic replacements (system/reveal/jailbreak)
    for (const p of patternToPlaceholder) {
      const before = outText;
      outText = outText.replace(p.regex, p.placeholder);
      if (before !== outText) replacements.push({ originalPattern: p.id, replacement: p.placeholder });
    }

    // 5) Remove/mark comment-style injections using compiled commentRegex
    const beforeComments = outText;
    outText = outText.replace(commentRegex, '[filtered:comment_injection]');
    if (beforeComments !== outText) replacements.push({ originalPattern: 'comment_injection', replacement: '[filtered:comment_injection]' });

    // ensure normalization.originalInput/normalized are set for downstream
    item.json.validation = item.json.validation || {};
    item.json.normalization = item.json.normalization || {};
    if(!item.json.validation.originalInput || String(item.json.validation.originalInput).trim()===''){
      item.json.validation.originalInput = primary || (item.json.chatInput||item.json.inputText||'');
    }
    item.json.normalization.normalized = outText;

    item.json.details = item.json.details || {};
    item.json.details.audit = item.json.details.audit || {};
    item.json.details.audit.sanitization = { sanitizedOutput: outText, replacements, processingTime: 0 };

    item.json.clearoutput = outText;
    item.json.injectiondetected = (replacements.length > 0) || (outText.indexOf('[filtered:') !== -1);
    item.json._debug_sanitization = { replacementsCount: replacements.length };
  }catch(err){
    item.json = item.json || {};
    item.json.details = item.json.details || {};
    item.json.details.audit = item.json.details.audit || {};
    item.json.details.audit.sanitization = item.json.details.audit.sanitization || {};
    item.json.details.audit.sanitization.error = err && err.message ? err.message : String(err);
    item.json._debug_sanitization_error = true;
  }
}
return items;
