// Enhanced Input Sanitizer v2.1 - Fixed for n8n compatibility
// Advanced protection against prompt injection, XSS, Unicode attacks, and data exfiltration

// Get input data using n8n v2 syntax
const inputItems = $input.all();
const outputs = [];

// Enhanced configuration with all security features
const DEFAULT_CONFIG = {
  // Core settings
  ENABLE_PROMPT_HEURISTICS: true,
  MASK_SECRETS: true,
  DETECT_TEMPLATE_INJECTION: true,
  AGGRESSIVE_DECODING: true,
  MAX_DECODE_ITERATIONS: 10,
  MAX_LENGTH: 5000,
  STRIP_ALL_TAGS: true,
  WHITELIST_TAGS: [],
  LOG_AUDIT: true,
  SANITIZE_LEVEL: 'maximum',

  // Enhanced pattern detection
  PROMPT_PATTERNS: [
    'ignore previous instructions',
    'ignore (?:the )?above instructions',
    'disregard (?:all )?(?:previous|prior) (?:instructions|messages)',
    'forget (?:everything|what)',
    'now reveal',
    'reveal (?:the )?secret',
    'api[_\\-]?key',
    'secret[:=]',
    'do not follow previous instructions',
    'ignore (?:this|the) instruction',
    'system prompt',
    'you are now',
    'act as',
    'pretend to be',
    'roleplay as',
    'bypass (?:the )?(?:filter|security)',
    'jailbreak',
    'dan mode',
    'developer mode'
  ],

  // Expanded secret detection
  SECRET_KEYWORDS: [
    'API_KEY', 'API-KEY', 'APIKEY',
    'SECRET', 'SECRET_KEY', 'SECRET-KEY',
    'TOKEN', 'ACCESS_TOKEN', 'REFRESH_TOKEN',
    'ACCESS_KEY', 'ACCESS-KEY',
    'PASSWORD', 'PASSWD', 'PWD',
    'PRIVATE_KEY', 'PRIVATE-KEY',
    'AUTH_TOKEN', 'AUTHORIZATION',
    'BEARER',
    'AWS_ACCESS_KEY_ID', 'AWS_SECRET_ACCESS_KEY',
    'DATABASE_URL', 'DB_PASSWORD',
    'ENCRYPTION_KEY', 'SIGNING_KEY'
  ],

  // Template injection patterns
  TEMPLATE_PATTERNS: [
    '\\{\\{.*\\}\\}',
    '\\$\\{.*\\}',
    '<%.*%>',
    '\\{%.*%\\}',
    '#\\{.*\\}',
    '\\$\\(.*\\)',
    '`.*`'
  ],

  // Dangerous schemes
  DANGEROUS_SCHEMES: [
    'javascript:', 'vbscript:', 'data:', 'blob:',
    'file:', 'about:', 'chrome:', 'chrome-extension:',
    'ms-appx:', 'ms-appx-web:', 'ms-local-stream:'
  ]
};

// === Helper Functions ===

function nowISO() { 
  return new Date().toISOString(); 
}

// Enhanced HTML entity decoder
function decodeHTMLEntities(str) {
  if (!str) return str;

  const entityMap = {
    'lt': '<', 'gt': '>', 'amp': '&', 'quot': '"', 'apos': "'",
    'nbsp': ' ', 'ndash': 'â€“', 'mdash': 'â€”', 'hellip': 'â€¦',
    'copy': 'Â©', 'reg': 'Â®', 'trade': 'â„¢'
  };

  return str.replace(/&(#x?[0-9A-Fa-f]+|[A-Za-z]+);/g, (match, group) => {
    if (!group) return match;

    if (group[0] === '#') {
      const isHex = group[1] === 'x' || group[1] === 'X';
      const num = isHex ? 
        parseInt(group.slice(2), 16) : 
        parseInt(group.slice(1), 10);

      if (!isNaN(num) && num >= 0 && num <= 0x10FFFF) {
        try {
          return String.fromCodePoint(num);
        } catch(e) {
          return match;
        }
      }
    }

    return entityMap[group.toLowerCase()] || match;
  });
}

// Aggressive iterative decoder
function aggressiveIterativeDecode(input, config) {
  if (!input) return input;

  let prev = null;
  let current = input;
  const maxIterations = config.AGGRESSIVE_DECODING ? 
    config.MAX_DECODE_ITERATIONS : 4;

  for (let i = 0; i < maxIterations && current !== prev; i++) {
    prev = current;

    // URL percent-encoding
    try {
      const decoded = decodeURIComponent(current);
      if (decoded !== current) current = decoded;
    } catch(e) {
      current = current.replace(/%([0-9A-Fa-f]{2})/g, (m, hex) => {
        try {
          return String.fromCharCode(parseInt(hex, 16));
        } catch(e) {
          return m;
        }
      });
    }

    // HTML entities
    current = decodeHTMLEntities(current);

    // Unicode escape sequences
    current = current.replace(/\\u([0-9A-Fa-f]{4})/g, (m, hex) => {
      return String.fromCharCode(parseInt(hex, 16));
    });

    // Hex escape sequences
    current = current.replace(/\\x([0-9A-Fa-f]{2})/g, (m, hex) => {
      return String.fromCharCode(parseInt(hex, 16));
    });

    // Plus-encoded spaces
    current = current.replace(/\+/g, ' ');
  }

  return current;
}

// Remove invisible and dangerous Unicode
function removeInvisibleAndDangerousChars(str) {
  if (!str) return str;

  str = str.normalize('NFKC');

  // Remove dangerous Unicode categories
  const patterns = [
    /[\u200B-\u200F]/g,  // Zero-width chars
    /[\u202A-\u202E]/g,  // BIDI control
    /[\u2060-\u206F]/g,  // Format chars
    /[\u2066-\u2069]/g,  // More BIDI
    /[\uFE00-\uFE0F]/g,  // Variation selectors
    /[\uFEFF]/g,          // BOM
    /[\uFFF9-\uFFFB]/g   // Interlinear annotation
  ];

  patterns.forEach(pattern => {
    str = str.replace(pattern, '');
  });

  // Remove control characters except newline and tab
  str = str.replace(/[\x00-\x08\x0B\x0C\x0E-\x1F\x7F-\x9F]/g, '');

  return str;
}

// Normalize URI schemes
function normalizeScheme(value) {
  if (!value) return value;

  let normalized = value;
  normalized = normalized.replace(/^["']|["']$/g, '');
  normalized = normalized.replace(/[\s\u0000-\u0020\u007F-\u009F]+/g, '');

  try {
    normalized = decodeURIComponent(normalized);
  } catch(e) {}

  normalized = decodeHTMLEntities(normalized);
  normalized = normalized.toLowerCase();

  return normalized;
}

// Enhanced attribute neutralization
function neutralizeAttributesEnhanced(html, config) {
  if (!html) return html;

  // Remove event handlers
  const eventHandlers = [
    'onclick', 'onerror', 'onload', 'onmouseover', 'onmouseout',
    'onmousemove', 'onmousedown', 'onmouseup', 'onkeydown', 'onkeyup',
    'onkeypress', 'onfocus', 'onblur', 'onchange', 'onsubmit',
    'ondblclick', 'ondrag', 'ondrop', 'oninput', 'onscroll'
  ];

  const eventPattern = new RegExp(
    `\\s(${eventHandlers.join('|')})\\s*=\\s*(?:'[^']*'|"[^"]*"|[^\\s>]+)`,
    'gi'
  );

  html = html.replace(eventPattern, '');

  // Remove dangerous styles
  html = html.replace(/\sstyle\s*=\s*(?:'[^']*'|"[^"]*"|[^\s>]+)/gi, '');

  // Neutralize dangerous URI attributes
  const uriAttrs = ['href', 'src', 'action', 'formaction', 'data'];
  const uriPattern = new RegExp(
    `\\s(${uriAttrs.join('|')})\\s*=\\s*(["']?)([^"'\\s>]+)\\2`,
    'gi'
  );

  html = html.replace(uriPattern, (match, attr, quote, value) => {
    const normalized = normalizeScheme(value);

    for (const scheme of config.DANGEROUS_SCHEMES) {
      if (normalized.startsWith(scheme)) {
        return ` ${attr}=${quote}#blocked${quote}`;
      }
    }

    if (normalized.startsWith('data:') && 
        /base64|text\/html|application\/x-|image\/svg/i.test(normalized)) {
      return ` ${attr}=${quote}#blocked${quote}`;
    }

    return match;
  });

  return html;
}

// Remove dangerous HTML elements
function removeDangerousElements(html) {
  if (!html) return html;

  const dangerousElements = [
    'script', 'iframe', 'object', 'embed', 'applet',
    'meta', 'link', 'style', 'base', 'form',
    'svg', 'math', 'canvas', 'audio', 'video'
  ];

  dangerousElements.forEach(tag => {
    const regex = new RegExp(
      `<${tag}\\b[^>]*>([\\s\\S]*?)<\\/${tag}>|<${tag}\\b[^>]*\\/?>`,
      'gi'
    );
    html = html.replace(regex, '');
  });

  // Remove comments and other constructs
  html = html.replace(/<!--[\s\S]*?-->/g, '');
  html = html.replace(/<\?[\s\S]*?\?>/g, '');
  html = html.replace(/<!DOCTYPE[^>]*>/gi, '');
  html = html.replace(/<!\[CDATA\[[\s\S]*?\]\]>/g, '');

  return html;
}

// Strip HTML tags
function stripTags(html, whitelist = []) {
  if (!html) return html;

  if (!whitelist || whitelist.length === 0) {
    return html.replace(/<\/?[^>]+>/g, '');
  }

  const allowedTags = whitelist
    .map(tag => tag.toLowerCase().replace(/[^a-z0-9]/g, ''))
    .join('|');

  if (!allowedTags) {
    return html.replace(/<\/?[^>]+>/g, '');
  }

  const pattern = new RegExp(
    `<\\/?(?!(?:${allowedTags})\\b)[^>]+>`,
    'gi'
  );

  return html.replace(pattern, '');
}

// Clean markdown
function cleanMarkdownSyntax(text) {
  if (!text) return text;

  text = text.replace(/!\[([^\]]*)\]\([^)]+\)/g, '$1');
  text = text.replace(/\[([^\]]+)\]\([^)]+\)/g, '$1');
  text = text.replace(/\[([^\]]+)\]\[[^\]]*\]/g, '$1');
  text = text.replace(/^\[[^\]]+\]:\s*.+$/gm, '');
  text = text.replace(/<[^>]+>/g, '');

  return text;
}

// Detect prompt injection
function detectPromptInjection(text, config) {
  if (!text || !config.ENABLE_PROMPT_HEURISTICS) {
    return { detected: false, severity: 'none', patterns: [] };
  }

  const detectedPatterns = [];
  let severity = 'none';

  for (const pattern of config.PROMPT_PATTERNS) {
    const regex = new RegExp(pattern, 'i');
    if (regex.test(text)) {
      detectedPatterns.push(pattern);
    }
  }

  if (config.DETECT_TEMPLATE_INJECTION) {
    for (const pattern of config.TEMPLATE_PATTERNS) {
      const regex = new RegExp(pattern, 'i');
      if (regex.test(text)) {
        detectedPatterns.push(`template: ${pattern}`);
      }
    }
  }

  if (detectedPatterns.length > 0) {
    if (detectedPatterns.length >= 3) {
      severity = 'critical';
    } else if (detectedPatterns.length >= 2) {
      severity = 'high';
    } else {
      severity = 'medium';
    }
  }

  return {
    detected: detectedPatterns.length > 0,
    severity,
    patterns: detectedPatterns
  };
}

// Mask secrets
function maskSecretsEnhanced(text, config) {
  if (!text || !config.MASK_SECRETS) return { text, maskedSecrets: [] };

  let maskedText = text;
  const maskedSecrets = [];

  const keywordPattern = config.SECRET_KEYWORDS
    .map(k => k.replace(/[-_]/g, '[-_]?'))
    .join('|');

  const secretPattern = new RegExp(
    `\\b(${keywordPattern})\\s*[:=]\\s*([A-Za-z0-9\\-_.=\\/+]+)`,
    'gi'
  );

  maskedText = maskedText.replace(secretPattern, (match, key, value) => {
    maskedSecrets.push(key);
    return `${key}: [REDACTED]`;
  });

  // Mask Bearer tokens
  maskedText = maskedText.replace(
    /\b(Bearer|bearer)\s+([A-Za-z0-9\-_.=\/+]+)/g,
    (match, bearer, token) => {
      maskedSecrets.push('Bearer Token');
      return `${bearer} [REDACTED]`;
    }
  );

  // Mask JWT tokens
  maskedText = maskedText.replace(
    /\b[A-Za-z0-9\-_]+\.[A-Za-z0-9\-_]+\.[A-Za-z0-9\-_]+\b/g,
    (match) => {
      const parts = match.split('.');
      if (parts.length === 3 && parts[0].length > 10) {
        maskedSecrets.push('JWT Token');
        return '[JWT_REDACTED]';
      }
      return match;
    }
  );

  return { text: maskedText, maskedSecrets };
}

// Main sanitization pipeline
function sanitizePipeline(input, config) {
  const transformations = [];
  const threats = [];
  let text = input === null || input === undefined ? '' : String(input);

  const originalText = text;
  const originalLength = text.length;

  // Step 1: Aggressive decoding
  const decoded = aggressiveIterativeDecode(text, config);
  if (decoded !== text) {
    transformations.push('aggressive_decode');
    text = decoded;
  }

  // Step 2: Remove invisible Unicode
  const cleanedUnicode = removeInvisibleAndDangerousChars(text);
  if (cleanedUnicode !== text) {
    transformations.push('unicode_cleaned');
    threats.push('invisible_chars');
    text = cleanedUnicode;
  }

  // Step 3: Neutralize HTML attributes
  const neutralized = neutralizeAttributesEnhanced(text, config);
  if (neutralized !== text) {
    transformations.push('attributes_neutralized');
    threats.push('event_handlers');
    text = neutralized;
  }

  // Step 4: Remove dangerous elements
  const safeElements = removeDangerousElements(text);
  if (safeElements !== text) {
    transformations.push('dangerous_elements_removed');
    threats.push('dangerous_html');
    text = safeElements;
  }

  // Snapshot before destructive clean/strip for detection
  const preDetectionText = text;

  // Step 5: Clean markdown
  const cleanedMarkdown = cleanMarkdownSyntax(text);
  if (cleanedMarkdown !== text) {
    transformations.push('markdown_cleaned');
    text = cleanedMarkdown;
  }

  // Step 6: Strip HTML tags
  if (config.STRIP_ALL_TAGS) {
    const stripped = stripTags(text, config.WHITELIST_TAGS);
    if (stripped !== text) {
      transformations.push('html_stripped');
      text = stripped;
    }
  }

  // Step 7: Normalize whitespace
  text = text.replace(/\s+/g, ' ').trim();
  if (text.length !== originalLength) {
    transformations.push('whitespace_normalized');
  }

  // Step 8: Detect prompt injection
  const promptInjection = detectPromptInjection(preDetectionText, config);
  if (promptInjection.detected) {
    threats.push('prompt_injection');
    transformations.push('prompt_injection_detected');
  }

  // Step 9: Mask secrets
  const secretMasking = maskSecretsEnhanced(text, config);
  if (secretMasking.maskedSecrets.length > 0) {
    text = secretMasking.text;
    threats.push('secrets_exposed');
    transformations.push('secrets_masked');
  }

  // Step 10: Length enforcement
  if (config.MAX_LENGTH && text.length > config.MAX_LENGTH) {
    text = text.slice(0, config.MAX_LENGTH);
    transformations.push('truncated');
  }

  // Calculate security score
  let securityScore = 100;
  securityScore -= threats.length * 15;
  securityScore -= transformations.length * 5;
  securityScore = Math.max(0, securityScore);

  // Determine security level
  let securityLevel = 'LOW';
  if (threats.includes('prompt_injection') || threats.includes('secrets_exposed')) {
    securityLevel = 'CRITICAL';
  } else if (threats.includes('dangerous_html') || threats.includes('event_handlers')) {
    securityLevel = 'HIGH';
  } else if (threats.length > 0) {
    securityLevel = 'MEDIUM';
  }

  return {
    text,
    transformations,
    threats,
    securityLevel,
    securityScore,
    promptInjection,
    originalLength,
    sanitizedLength: text.length,
    secretsMasked: secretMasking?.maskedSecrets || []
  };
}

// === Main Processing Loop ===
for (let idx = 0; idx < inputItems.length; idx++) {
  const item = inputItems[idx];
  const json = item.json || {};

  // Merge configurations
  const config = Object.assign({}, DEFAULT_CONFIG, json.sanitizer_config || {});

  // Ensure arrays are properly initialized
  if (!Array.isArray(config.PROMPT_PATTERNS)) {
    config.PROMPT_PATTERNS = DEFAULT_CONFIG.PROMPT_PATTERNS;
  }
  if (!Array.isArray(config.SECRET_KEYWORDS)) {
    config.SECRET_KEYWORDS = DEFAULT_CONFIG.SECRET_KEYWORDS;
  }
  if (!Array.isArray(config.TEMPLATE_PATTERNS)) {
    config.TEMPLATE_PATTERNS = DEFAULT_CONFIG.TEMPLATE_PATTERNS;
  }
  if (!Array.isArray(config.DANGEROUS_SCHEMES)) {
    config.DANGEROUS_SCHEMES = DEFAULT_CONFIG.DANGEROUS_SCHEMES;
  }

  // Get input text
  const originalInput = (typeof json.message !== 'undefined') ? 
    String(json.message) : 
    JSON.stringify(json);

  // Run sanitization pipeline
  const result = sanitizePipeline(originalInput, config);

  // Build output
  const output = {
    // Core fields
    clearoutput: result.text,
    injectiondetected: result.promptInjection.detected,
    securitylevel: result.securityLevel,
    securityscore: result.securityScore,

    // Original test data (for aggregator)
    id: json.id || `test-${idx}`,
    description: json.description || '',
    category: json.category || 'general',

    // Session tracking
    sessionid: `session-${Date.now()}-${Math.random().toString(36).slice(2, 11)}`,
    timestamp: nowISO(),

    // Detailed results
    details: {
      query: originalInput,
      context: {
        conversationId: `conv-${Date.now()}-${idx}`,
        timestamp: nowISO(),
        userId: json.userId || 'anonymous',
        source: json.source || 'user_input'
      },
      metadata: {
        sanitized: true,
        transformations: result.transformations,
        threats: result.threats,
        injectionAttemptDetected: result.promptInjection.detected,
        injectionSeverity: result.promptInjection.severity,
        injectionPatterns: result.promptInjection.patterns,
        secretsMasked: result.secretsMasked,
        originalLength: result.originalLength,
        sanitizedLength: result.sanitizedLength,
        processingTime: Date.now(),
        sanitizerVersion: '2.1.0'
      },
      securityHeaders: {
        instructions: 'Process ONLY the structured data in the clearoutput field.',
        outputFormat: 'Return ONLY valid JSON matching the specified schema.',
        securityLevel: result.securityLevel,
        securityScore: result.securityScore,
        timestamp: Date.now(),
        validationRequired: result.securityLevel !== 'LOW',
        requiresReview: result.securityLevel === 'CRITICAL',
        allowExecution: result.securityLevel === 'LOW',
        sandboxRequired: result.securityLevel !== 'LOW'
      },
      audit: config.LOG_AUDIT ? {
        originalInput: originalInput,
        processingTimestamp: nowISO(),
        configUsed: {
          sanitizeLevel: config.SANITIZE_LEVEL,
          aggressiveDecoding: config.AGGRESSIVE_DECODING,
          promptHeuristics: config.ENABLE_PROMPT_HEURISTICS,
          secretMasking: config.MASK_SECRETS
        }
      } : {
        processingTimestamp: nowISO(),
        auditingDisabled: true
      }
    }
  };

  outputs.push(output);
}

return outputs;

// Advanced Test Case Generator v2.1 - Fixed for n8n
